// src/router/index.js
import { createRouter, createWebHistory } from 'vue-router';

// Impor komponen View Anda
const HomeView = () => import('@/views/HomeView.vue');
const ArticlesView = () => import('@/views/ArticlesView.vue');
const ArticleDetailView = () => import('@/views/ArticleDetailView.vue');
const AboutView = () => import('@/views/AboutView.vue');
const NotFoundView = () => import('@/views/NotFoundView.vue');
const ServicesView = () => import('@/views/ServicesView.vue');
const ServiceDetailView = () => import('@/views/ServiceDetailView.vue');
const ReservationView = () => import('@/views/ReservationView.vue');

// --- PASTIKAN VIEW BARU DI-IMPOR DI SINI ---
const ForumView = () => import('@/views/ForumView.vue');
const QuestionDetailView = () => import('@/views/QuestionDetailView.vue');
const AskQuestionView = () => import('@/views/AskQuestionView.vue');

const routes = [
  { path: '/', name: 'Home', component: HomeView },
  { path: '/articles', name: 'Articles', component: ArticlesView },
  { path: '/articles/:id', name: 'ArticleDetail', component: ArticleDetailView },
  { path: '/layanan', name: 'Services', component: ServicesView },
  { path: '/layanan/:slug', name: 'ServiceDetail', component: ServiceDetailView },
  { path: '/reservasi', name: 'Reservation', component: ReservationView },
  { path: '/about', name: 'About', component: AboutView },
  
  // --- PASTIKAN RUTE BARU SUDAH DIDAFTARKAN DI SINI ---
  {
    path: '/tanya-bidan',
    name: 'Forum',
    component: ForumView,
  },
  {
    path: '/tanya-bidan/tanya', // Rute untuk form bertanya
    name: 'AskQuestion',
    component: AskQuestionView,
  },
  {
    path: '/tanya-bidan/:id', // Rute untuk detail pertanyaan
    name: 'QuestionDetail',
    component: QuestionDetailView,
  },
  {
    path: '/articles',
    name: 'Articles',
    component: ArticleView,
  },
  {
    path: '/articles/:id', // Here, ':id' will capture the slug
    name: 'ArticleDetail',
    component: ArticleDetailView,
    props: true // This passes route params as props to the component, though you're using useRoute() directly.
  },
  // ---------------------------------------------------

  // Rute untuk konsultasi yang lama seharusnya sudah dihapus
  // { path: '/konsultasi', ... } 

  { path: '/:pathMatch(.*)*', name: 'NotFound', component: NotFoundView },
];

const router = createRouter({
  history: createWebHistory(process.env.BASE_URL),
  routes,
  scrollBehavior() {
    return { top: 0 };
  },
});

export default router;
