// src/router/index.js
import { createRouter, createWebHistory } from 'vue-router';

// Impor komponen View Anda
const HomeView = () => import('@/views/HomeView.vue');
const ArticlesView = () => import('@/views/ArticlesView.vue');
const ArticleDetailView = () => import('@/views/ArticleDetailView.vue');
const ConsultationView = () => import('@/views/ConsultationView.vue');
const AboutView = () => import('@/views/AboutView.vue');
const NotFoundView = () => import('@/views/NotFoundView.vue');

// Impor view BARU
const ServicesView = () => import('@/views/ServicesView.vue');
const ServiceDetailView = () => import('@/views/ServiceDetailView.vue');
const ReservationView = () => import('@/views/ReservationView.vue'); // Ini tetap form

const routes = [
  { path: '/', name: 'Home', component: HomeView },
  { path: '/articles', name: 'Articles', component: ArticlesView },
  { path: '/articles/:id', name: 'ArticleDetail', component: ArticleDetailView },
  
  // --- PERUBAHAN DI SINI ---
  { path: '/layanan', name: 'Services', component: ServicesView }, // Halaman galeri layanan
  { path: '/layanan/:slug', name: 'ServiceDetail', component: ServiceDetailView }, // Halaman detail layanan
  { path: '/reservasi', name: 'Reservation', component: ReservationView }, // Halaman form reservasi
  // -------------------------

  { path: '/konsultasi', name: 'Consultation', component: ConsultationView },
  { path: '/about', name: 'About', component: AboutView },
  { path: '/:pathMatch(.*)*', name: 'NotFound', component: NotFoundView },
];

const router = createRouter({
  history: createWebHistory(process.env.BASE_URL),
  routes,
  scrollBehavior() {
    return { top: 0 };
  },
});

export default router;
