// src/router/index.js
import { createRouter, createWebHistory } from 'vue-router';

// Impor komponen View Anda
// Kita gunakan dynamic import (lazy loading) untuk performa yang lebih baik
const HomeView = () => import('@/views/HomeView.vue');
const ArticlesView = () => import('@/views/ArticlesView.vue');
const ArticleDetailView = () => import('@/views/ArticleDetailView.vue');
const ReservationView = () => import('@/views/ReservationView.vue');
const ConsultationView = () => import('@/views/ConsultationView.vue');
const AboutView = () => import('@/views/AboutView.vue');
const NotFoundView = () => import('@/views/NotFoundView.vue');

const routes = [
  {
    path: '/',
    name: 'Home',
    component: HomeView,
  },
  {
    path: '/articles',
    name: 'Articles',
    component: ArticlesView,
  },
  {
    path: '/articles/:id', // Rute dinamis untuk detail artikel
    name: 'ArticleDetail',
    component: ArticleDetailView,
  },
  {
    path: '/reservation',
    name: 'Reservation',
    component: ReservationView,
  },
  {
    path: '/consultation',
    name: 'Consultation',
    component: ConsultationView,
  },
  {
    path: '/about',
    name: 'About',
    component: AboutView,
  },
  {
    path: '/:pathMatch(.*)*', // Regex untuk menangkap semua URL yang tidak cocok
    name: 'NotFound',
    component: NotFoundView,
  },
];

const router = createRouter({
  history: createWebHistory(process.env.BASE_URL),
  routes,
  // Fungsi ini akan membuat halaman scroll ke atas setiap kali pindah rute
  scrollBehavior(to, from, savedPosition) {
    if (savedPosition) {
      return savedPosition;
    } else {
      return { top: 0 };
    }
  },
});

export default router;
