}

/* Why Us Section */
.why-us-section {
  background-color: #fdf8f9;
}

.features-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(260px, 1fr));
  gap: 40px;
  margin-top: 60px;
}

.feature-card {
  background: #fff;
  padding: 30px;
  border-radius: 16px;
  border: 1px solid #f8bbd0;
  text-align: center;
  box-shadow: 0 4px 16px rgba(0,0,0,0.05);
  transition: transform 0.3s ease;
}

.feature-card:hover {
  transform: translateY(-8px);
}

.feature-icon {
