// src/services/ApiService.js
import axios from 'axios';

// Konfigurasi instance Axios dengan URL dasar ke API backend
const apiClient = axios.create({
  baseURL: 'http://localhost:5000/api',
});

// Anda bisa menambahkan interceptor untuk menyisipkan token secara otomatis
// apiClient.interceptors.request.use(config => {
//   const userId = localStorage.getItem('user_id'); // Ambil ID user dari local storage
//   if (userId) {
//     config.headers['x-user-id'] = userId;
//   }
//   return config;
// });

export default {
  // === Articles ===
  getArticles() {
    return apiClient.get('/articles');
  },
  getArticle(id) {
    return apiClient.get(`/articles/${id}`);
  },
  // Untuk rute yang butuh otentikasi, kita bisa tambahkan header secara manual
  // Di aplikasi nyata, ini akan ditangani oleh interceptor di atas
  createArticle(articleData, userId) {
    return apiClient.post('/articles', articleData, {
      headers: { 'x-user-id': userId }
    });
  },

  // === Categories ===
  getCategories() {
    return apiClient.get('/categories');
  },

  // === Consultations ===
  submitConsultation(consultationData) {
    return apiClient.post('/consultations', consultationData);
  },

  // === Reservations ===
  submitReservation(reservationData) {
    return apiClient.post('/reservations', reservationData);
  },
  
  // === Testimonials ===
  getTestimonials() {
    return apiClient.get('/testimonials');
  },
};
