// src/services/ApiService.js
import axios from 'axios';
import store from '@/store'; // Impor store Vuex

const apiClient = axios.create({
  baseURL: 'http://localhost:5000/api',
});

// ### INTERCEPTOR - INI SANGAT PENTING ###
// Fungsi ini akan berjalan sebelum setiap request dikirim
apiClient.interceptors.request.use(config => {
  const token = store.state.auth.token; // Ambil token dari Vuex
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  return config;
}, error => {
  return Promise.reject(error);
});


export default {
  // === Auth Service ===
  register(userData) {
    return apiClient.post('/auth/register', userData);
  },
  login(userData) {
    return apiClient.post('/auth/login', userData);
  },
  getMe() {
    return apiClient.get('/auth/me');
  },

  // === Articles Service ===
  getArticles() {
    return apiClient.get('/articles');
  },
  getArticle(id) {
    return apiClient.get(`/articles/${id}`);
  },

  // === Categories Service ===
  getCategories() {
      return apiClient.get('/categories');
  },

  // === Forum (Tanya Bidan) Service ===
  getAllQuestions() {
    return apiClient.get('/tanya-bidan');
  },
  getQuestionById(id) {
    return apiClient.get(`/tanya-bidan/${id}`);
  },
  createQuestion(questionData) {
    return apiClient.post('/tanya-bidan', questionData);
  },
  createAnswer(questionId, answerData) {
    return apiClient.post(`/tanya-bidan/${questionId}/answers`, answerData);
  },

  // === Reservations Service ===
  submitReservation(reservationData) {
    return apiClient.post('/reservations', reservationData);
  },
};
