// src/store/modules/articles.js
import ApiService from '@/services/ApiService';

// State: Tempat menyimpan data
const state = {
  articles: [],
  status: 'idle', // 'idle', 'loading', 'success', 'error'
};

// Getters: Untuk mengakses state (bisa dengan komputasi)
const getters = {
  allArticles: state => state.articles,
  articleCount: state => state.articles.length,
};

// Actions: Tempat untuk logika asynchronous (misal: memanggil API)
const actions = {
  async fetchArticles({ commit }) {
    commit('SET_STATUS', 'loading');
    try {
      const response = await ApiService.getArticles();
      commit('SET_ARTICLES', response.data);
      commit('SET_STATUS', 'success');
    } catch (error) {
      console.error('Error fetching articles in Vuex:', error);
      commit('SET_STATUS', 'error');
    }
  },
};

// Mutations: Satu-satunya cara untuk mengubah state (harus synchronous)
const mutations = {
  SET_ARTICLES(state, articles) {
    state.articles = articles;
  },
  SET_STATUS(state, status) {
    state.status = status;
  },
};

export default {
  namespaced: true, // PENTING untuk membuat modul ini terisolasi
  state,
  getters,
  actions,
  mutations,
};
